import React from 'react';

import './write.scss';
import WriteImage from '../../../assets/images/post-image.jpg'

export const Write = () => {
  return (
        <div className='ui-write'>
            <img className='ui-write__image' src={ WriteImage } alt="Write Pic" />
            <form className="ui-write__form">
                <div className="ui-write__form__group">
                    <label className='ui-write__form__group__label' htmlFor="fileInput"><i class="fas fa-plus"></i></label>
                    <input className='ui-write__form__group__input' type="file" id='fileInput' style={ { display: 'none' } } />
                    <input className='ui-write__form__group__input' type="text" placeholder='Title' />
                </div>
                <div className="ui-write__form__group">
                    <input type="textarea" placeholder='Tell Your Story...' />
                </div>
                <button className='ui-write__form__submitBtn' >Publish</button>
            </form>
        </div>
    );
};
